create database cms_dev;

use cms_dev;

--- 创建页面模块数据表

create table `banners`(
  `id` int not null auto_increment,
  `type` int not null,
  `title` text default null comment '主标题',
  `title_en` text default null comment '主标题（英）',
  `subtitle` text default null comment '副标题',
  `subtitle_en` text default null comment '副标题（英）',
  `content` text default null comment '内容',
  `content_en` text default null comment '内容（英）',
  `description` text default null comment '描述介绍',
  `description_en` text default null comment '描述介绍（英）',
  `content_origin` int default 1 comment '中文内容来源：1-后台配置，2-语雀',
  `content_origin_id` int default null comment '中文内容来源 id',
  `content_en_origin` int default 1 comment '英文内容来源：1-后台配置，2-语雀',
  `content_en_origin_id` int default null comment '英文内容来源 id',
  `status` int default 1 comment '轮播状态：1-展示中，0-已下线',
  `url` varchar(255) default '' comment '图片地址',
  `url_en` varchar(255) default '' comment '图片地址（英）',
  `href` varchar(255) default '' comment '点击跳转地址',
  `icon` varchar(255) default '' comment '小图标地址',
  `authority` text default null comment '可看权限的用户 id 集合',
  `created_at` datetime default null comment '创建时间',
  `updated_at` datetime default null comment '更新时间',
  primary key(`id`)
)engine=InnoDB auto_increment=1 default charset=utf8 comment='页面模块数据表';

